import React, {useState, useEffect} from 'react'
import {useParams, useNavigate} from 'react-router-dom'
import styles from './Detail.module.css'

export default function Detail(props) {
    const navigate = useNavigate()
    const {detailId} = useParams()
    const [character, setCharacter] = useState({})

    useEffect(()=>{
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
        .then((res)=> res.json())
        .then((data)=>{
            data.name ? setCharacter(data) : window.alert("No hay personaje con ese ID")
        })
        .catch((error)=>{
            console.log(error)
            alert("Ups algo fallo")
        })
        },[]);
        
    
    return(
        <div>
            <div className={styles }></div>
            <button onClick={() => navigate(-1)}>Regresar</button>
            <h1 className= {styles.container}>Name:{character.name}</h1>
            <h2 className= {styles.detalle}>Status:{character.status}</h2>
            <h2>Specie: {character.species}</h2>
            <h2>Gender: {character.gender}</h2>
            <h2>Origin: {character.origin?.name}</h2>
            <img src={character.image} alt="Not Found"/>

        </div>
    )
}
