import React, {useState} from 'react';
import styles from './SearchBar.module.css'

export default function SearchBar(props) {

   const [character, setCharacter] = useState("")

   function handleRandomSearch(event) {
      event.preventDefault();
      const maxId = 800;  // El máximo id en tu base de datos
      const randomId = Math.floor(Math.random() * maxId) + 690;  // Genera un número aleatorio entre 1 y maxId
      props.onSearch(randomId);
    }

const handleInputChange = (e)=>{
   const {value} = e.target
   setCharacter(value)

}

   return (
      <div className={styles.containerSearch}>
          <input className={styles.input} type='search' onChange={handleInputChange}/>
         <button className={styles.button} onClick={()=> props.onSearch(character)}>Agregar</button> 
         <button className={styles.aleatorio} onClick={()=> props.onSearch(character)} onChange={handleRandomSearch}>Aleatorio</button>
      </div>
   );
}
